<script type="text/javascript">
import { get } from "../helpers/api";
export default {
  props: {
    value: {
      type: [String, File],
      default: null
    }
  },
  data() {
    return {
      education: [],
      editeducation: {
        institution_name: null,
        degree: null,
        course: null,
        start_date: null,
        end_date: null
      },
      neweducation: {
        institution_name: null,
        degree: null,
        course: null,
        start_date: null,
        end_date: null
      }
    };
  },
  mounted() {
    // this.Edudata();
  },
  created() {
    get("/api/education").then(res => {
      console.log(res);
      this.education = res.data.education;
    });
  },
  methods: {
    save() {
      const form = toMulipartedForm(this.form, this.$route.meta.mode);
      post(this.storeURL, form)
        .then(res => {
          if (res.data.saved) {
            Flash.setSuccess(res.data.message);
            this.$router.push(`/profile`);
          }
          this.isProcessing = false;
        })
        .catch(err => {
          if (err.response.status === 422) {
            this.error = err.response.data;
          }
          this.isProcessing = false;
        });
    },
    addEducation() {
      this.education.push({
        institution_name: "",
        degree: "",
        course: "",
        start: "",
        end: ""
      });
    },
    remove(type, index) {
      if (this.education.length > 0) {
        this.education.splice(index, 1);
      }
    },
    edit(edudata) {
      this.editeducation.institution_name = edudata.institution_name;
      this.editeducation.degree = edudata.degree;
      this.editeducation.course = edudata.course;
      this.editeducation.start = edudata.start_date;
      this.editeducation.end = edudata.end_date;
    }
  }
};
</script>
